    const storedUsername = localStorage.getItem("katmannames_username");
    if (!storedUsername) {
      setLocation("/");
      return;
    }
    setUsername(storedUsername);
  }, [setLocation]);

  useEffect(() => {
    if (isConnected) {
      send("list_rooms", {});

      const pollInterval = setInterval(() => {
        send("list_rooms", {});
      }, 3000);

      return () => clearInterval(pollInterval);
    }
  }, [isConnected, send]);

  useEffect(() => {
    if (gameState) {
      setLocation("/game");
    }
  }, [gameState, setLocation]);

  const handleJoinRoom = (roomCode: string, hasPassword: boolean) => {
    if (hasPassword) {
      const password = prompt("Bu oda ÅŸifreli. LÃ¼tfen ÅŸifreyi girin:");
      if (!password) return;

      send("join_room", {
        roomCode,
        username,
        password,
        playerId: localStorage.getItem("katmannames_player_id") || undefined,
      });
    } else {
      send("join_room", {
        roomCode,
        username,
        playerId: localStorage.getItem("katmannames_player_id") || undefined,
      });
    }
  };

  const getPhaseText = (phase: string) => {
    switch (phase) {
      case "lobby":
        return "Beklemede";
      case "playing":
        return "OynanÄ±yor";
      case "ended":
        return "Bitti";
      default:
        return phase;
    }
  };

  return (
    <div className="min-h-screen relative overflow-hidden">
      <div 
        className="absolute inset-0 bg-cover bg-center bg-no-repeat" 
        style={{ backgroundImage: 'url(/arkaplan.png)' }}
      />

      {/* Particles */}
      {[...Array(70)].map((_, i) => (
        <div key={i} className={`particle particle-${i + 1}`} />
      ))}

      {/* Light Effects */}
      <div className="light-effect light-1" />
      <div className="light-effect light-2" />
      <div className="light-effect light-3" />
      <div className="light-effect light-4" />
      <div className="light-effect light-5" />

      <div className="relative z-10 min-h-screen flex flex-col items-center justify-center p-8">
        <Card className="w-full max-w-4xl bg-slate-900/90 backdrop-blur-lg border-orange-900/30 shadow-2xl">
          <div className="p-8">
            <h1 className="text-4xl font-bold text-center mb-2 text-white">
              Odalar
            </h1>
            <p className="text-center text-slate-300 mb-6">
              Bir odaya katÄ±l veya yeni oda oluÅŸtur
            </p>

            <div className="flex gap-4 mb-6">
              <Button 
                onClick={() => setShowCreateModal(true)}
                className="flex-1 bg-blue-600 hover:bg-blue-700 text-white font-semibold py-3"
              >
                Yeni Oda OluÅŸtur
              </Button>
              <Button 
                onClick={() => setShowJoinModal(true)}
                className="flex-1 bg-red-600 hover:bg-red-700 text-white font-semibold py-3"
              >
                Oda Kodu ile KatÄ±l
              </Button>
            </div>

            <div className="space-y-3 max-h-[500px] overflow-y-auto pr-2">
              {roomsList.length === 0 ? (
                <div className="text-center py-12 text-slate-400">
                  HenÃ¼z aktif oda yok. Ä°lk odayÄ± sen oluÅŸtur!
                </div>
              ) : (
                roomsList.map((room: RoomListItem) => (
                  <Card 
                    key={room.roomCode}
                    className="bg-slate-800/60 border-slate-700 hover:bg-slate-800/80 transition-all cursor-pointer"
                    onClick={() => handleJoinRoom(room.roomCode, room.hasPassword)}
                  >
                    <div className="p-4 flex items-center justify-between">
                      <div className="flex items-center gap-4">
                        <div className="text-2xl font-bold text-orange-400">
                          {room.roomCode}
                        </div>
                        <div className="flex items-center gap-2 text-slate-300">
                          <Users size={18} />
                          <span>{room.playerCount} oyuncu</span>
                        </div>
                        {room.hasPassword && (
                          <div className="flex items-center gap-1 text-yellow-400">
                            <Lock size={16} />
                            <span className="text-sm">Åifreli</span>
                          </div>
                        )}
                      </div>
                      <div className="flex items-center gap-3">
                        <div className={`px-3 py-1 rounded-full text-sm font-medium ${
                          room.phase === "lobby" 
                            ? "bg-green-900/50 text-green-300" 
                            : "bg-blue-900/50 text-blue-300"
                        }`}>
                          {room.phase === "lobby" && <span className="mr-1">â—</span>}
                          {room.phase === "playing" && <Play size={14} className="inline mr-1" />}
                          {getPhaseText(room.phase)}
                        </div>
                        <Button 
                          size="sm" 
                          className="bg-orange-600 hover:bg-orange-700"
                          onClick={(e) => {
                            e.stopPropagation();
                            handleJoinRoom(room.roomCode, room.hasPassword);
                          }}
                        >
                          KatÄ±l
                        </Button>
                      </div>
                    </div>
                  </Card>
                ))
              )}
            </div>
          </div>
        </Card>
      </div>

      {showCreateModal && (
        <CreateRoomModal 
          username={username}
          onClose={() => setShowCreateModal(false)}
          send={send}
        />
      )}

      {showJoinModal && (
        <JoinRoomModal 
          username={username}
          onClose={() => setShowJoinModal(false)}
          send={send}
        />
      )}
    </div>
  );
}

function CreateRoomModal({ username, onClose, send }: { 
  username: string; 
  onClose: () => void; 
  send: (type: string, payload: any) => void;
}) {
  const [password, setPassword] = useState("");
  const [hasPassword, setHasPassword] = useState(false);

  const handleCreate = () => {
    send("create_room", {
      username,
      password: hasPassword ? password : undefined,
    });
    onClose();
  };

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/60 backdrop-blur-sm">
      <Card className="w-full max-w-md bg-slate-900/95 border-orange-900/30">
        <div className="p-6">
          <h2 className="text-2xl font-bold text-white mb-4">Yeni Oda OluÅŸtur</h2>

          <div className="space-y-4">
            <div className="flex items-center gap-2">
              <input 
                type="checkbox"
                id="hasPassword"
                checked={hasPassword}
                onChange={(e) => setHasPassword(e.target.checked)}
                className="w-4 h-4"
              />
              <label htmlFor="hasPassword" className="text-slate-300">
                Åifreli oda oluÅŸtur
              </label>
            </div>

            {hasPassword && (
              <div>
                <label className="block text-sm font-medium text-slate-300 mb-2">
                  Oda Åifresi
                </label>
                <input 
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Åifre girin"
                  className="w-full px-4 py-2 bg-slate-800 border border-slate-700 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-orange-500"
                />
              </div>
            )}

            <div className="flex gap-3 mt-6">
              <Button 
                onClick={onClose}
                variant="outline"
                className="flex-1"
              >
                Ä°ptal
              </Button>
              <Button 
                onClick={handleCreate}
                className="flex-1 bg-blue-600 hover:bg-blue-700"
                disabled={hasPassword && !password}
              >
                OluÅŸtur
              </Button>
            </div>
          </div>
        </div>
      </Card>
    </div>
  );
}

function JoinRoomModal({ username, onClose, send }: { 
  username: string; 
  onClose: () => void; 
  send: (type: string, payload: any) => void;
}) {
  const [roomCode, setRoomCode] = useState("");
  const [password, setPassword] = useState("");
  const [needsPassword, setNeedsPassword] = useState(false);

  const handleJoin = () => {
    if (!roomCode.trim()) return;

    send("join_room", {
      roomCode: roomCode.toUpperCase(),
      username,
      password: needsPassword ? password : undefined,
      playerId: localStorage.getItem("katmannames_player_id") || undefined,
    });
    onClose();
  };

  return (
    <div className="fixed inset-0 z-50 flex items-center justify-center bg-black/60 backdrop-blur-sm">
      <Card className="w-full max-w-md bg-slate-900/95 border-orange-900/30">
        <div className="p-6">
          <h2 className="text-2xl font-bold text-white mb-4">Oda Kodu ile KatÄ±l</h2>

          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-slate-300 mb-2">
                Oda Kodu
              </label>
              <input 
                type="text"
                value={roomCode}
                onChange={(e) => setRoomCode(e.target.value.toUpperCase())}
                placeholder="Ã–rn: ABC123"
                maxLength={6}
                className="w-full px-4 py-2 bg-slate-800 border border-slate-700 rounded-lg text-white text-center text-2xl font-bold focus:outline-none focus:ring-2 focus:ring-orange-500"
              />
            </div>

            <div className="flex items-center gap-2">
              <input 
                type="checkbox"
                id="needsPassword"
                checked={needsPassword}
                onChange={(e) => setNeedsPassword(e.target.checked)}
                className="w-4 h-4"
              />
              <label htmlFor="needsPassword" className="text-slate-300">
                Oda ÅŸifreli
              </label>
            </div>

            {needsPassword && (
              <div>
                <label className="block text-sm font-medium text-slate-300 mb-2">
                  Oda Åifresi
                </label>
                <input 
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  placeholder="Åifre girin"
                  className="w-full px-4 py-2 bg-slate-800 border border-slate-700 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-orange-500"
                />
              </div>
            )}

            <div className="flex gap-3 mt-6">
              <Button 
                onClick={onClose}
                variant="outline"
                className="flex-1"
              >
                Ä°ptal
              </Button>
              <Button 
                onClick={handleJoin}
                className="flex-1 bg-red-600 hover:bg-red-700"
                disabled={!roomCode.trim() || (needsPassword && !password)}
              >
                KatÄ±l
              </Button>
            </div>
          </div>
        </div>
      </Card>
    </div>
  );
}
